    private void btokActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btokActionPerformed
        tfcodegr.setBackground(Color.white);
        tfintitulegr.setBackground(Color.white);
        tfniveau.setBackground(Color.white);

        boolean erreur = false;
        String codegr = tfcodegr.getText();
        if (codegr.trim().equals("")) {
            erreur = true;
            tfcodegr.setBackground(Color.ORANGE);
        }
        String intitule = tfintitulegr.getText();
        if (intitule.trim().equals("")) {
            erreur = true;
            tfintitulegr.setBackground(Color.ORANGE);
        }
        String niveau = tfniveau.getText();
        if (niveau.trim().equals("")) {
            erreur = true;
            tfniveau.setBackground(Color.ORANGE);
        }

         if (!erreur) {
            Groupe g = new Groupe(codegr, intitule, niveau);
            
            String msg = m.ajoutGroupe(g);
            JOptionPane.showMessageDialog(this, msg, "RÃ©sultat", JOptionPane.INFORMATION_MESSAGE);
        } 
         else {
            if(erreur) JOptionPane.showMessageDialog(this, "Veuillez remplir tous les champs", "Erreur", JOptionPane.ERROR_MESSAGE);
            
        }
        
        

    }//GEN-LAST:event_btokActionPerformed

